CREATE DATABASE retaggru_rgffxiv;

USE retaggru_rgffxiv;

CREATE TABLE bozjafrags (
	id TINYINT PRIMARY KEY NOT NULL,
	actionicons VARCHAR(50) NOT NULL,
    actions VARCHAR(50) NOT NULL,
    fragicons VARCHAR(50) NOT NULL,
    fragments VARCHAR(50) NOT NULL,
    resistancerank TINYINT NOT NULL,
	sourcezone VARCHAR(50) NOT NULL,
    sourcedrop VARCHAR(100) NOT NULL
);

CREATE TABLE bozja_actions (
	actionicons VARCHAR(50) NOT NULL,
    actions VARCHAR(50) NOT NULL,
    castrange VARCHAR(50) NOT NULL,
    radius VARCHAR(50) NOT NULL,
    cast VARCHAR(50) NOT NULL,
    recast VARCHAR(50) NOT NULL,
    available VARCHAR(50) NOT NULL,
    affinity VARCHAR(50) NOT NULL,
    tooltip VARCHAR(250) NOT NULL
);

UPDATE bozjafrags SET actionicons='lostslash.png' WHERE id=4;

